import React, { useState } from 'react';
import axios from 'axios';
import { marked } from 'marked';
// import Markdown from 'marked-react';
// import marked from 'marked';

function Book() {
  const [bookName, setBookName] = useState('');
  const [bookInfo, setBookInfo] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://127.0.0.1:5000/info//${bookName}`);
      setBookInfo(response.data);
    //   setBookInfo(marked(bookInfo.data))
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Book Search</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={bookName}
          onChange={(event) => setBookName(event.target.value)}
          placeholder="Enter book name"
        />
        <button type="submit">Search</button>
      </form>
      {bookInfo && (
        <div>
            {/* <script src="https://cdn.jsdelivr.net/npm/marked@4.0.1/dist/marked.min.js"></script> */}
            {/* <script>{setBookInfo(marked.parse(bookInfo.data))}</script> */}

          <h2>{bookName}</h2>
          {/* <p dangerouslySetInnerHTML={{ __html: bookInfo }} /> */}
          <p style={{textAlign:'left', marginRight:'50px', padding:'5px',fontFamily:'serif'}}>{bookInfo.data}</p>
        </div>
      )}
    </div>
  );
}

export default Book;
