import axios from 'axios';

function Book() {
  const [bookName, setBookName] = useState('');
  const [bookInfo, setBookInfo] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`https://your-backend-server.com/api/books/${bookName}`);
      setBookInfo(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Book Search</h1>
      
    </div>
  );
}

export default Book;
