import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { marked } from 'marked';
import Container from 'react-bootstrap/esm/Container';
// import Markdown from 'marked-react';
// import marked from 'marked';
import './animation.css'
import { TypeAnimation } from 'react-type-animation';

function Book() {
  const [bookName, setBookName] = useState('');
  const [bookInfo, setBookInfo] = useState({});
  const [bookUrl, setBookUrl]=useState();

//   useEffect(() => {
// const typewriter = document.getElementById('typewriter');
// let text=bookInfo
// let index = 0;
// function type() {
//   if (index < text.length) {
//     typewriter.innerHTML = text.slice(0, index) + '<span class="blinking-cursor">|</span>';
//     index++;
//     setTimeout(type, Math.random() * 150 + 50);
//     } else {
//       typewriter.innerHTML = text.slice(0, index) + '<span class="blinking-cursor">|</span>';
//     }
// }
// // start typing
// type();
//   }, [bookInfo]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://127.0.0.1:5000/info/${bookName}`);
      setBookInfo(marked(response.data.data));
      // setBookInfo({__html:marked(response.data.data)})
      // console.log("title:",bookInfo.data.title)
      const bookmeta = await axios.get(`http://127.0.0.1:5000/bookinfo`);
      // console.log("title:",bookmeta.data)

      const cover_img=await   axios.get(`https://bookcover.longitood.com/bookcover`, {
        params: {
            book_title: bookmeta.data.title,
            author_name: bookmeta.data.author
        }
    })
    setBookUrl(cover_img.data.url)
    console.log("url:",bookUrl)
    const questions = await axios.get(`http://127.0.0.1:5000/questions`);
    alert(questions.data.q1)
    console.log(questions.data.q2)


    } catch (error) {
      console.error(error);
    }
  };

  setTimeout(function() {
    const bookmetainfo=async()=>{
    try {
      const questions = await axios.get(`http://127.0.0.1:5000/questions`);
      alert(questions.data)
      
    } catch (error) {
      console.error(error);
    }
    console.log('This message will be displayed after 3 seconds');
    bookmetainfo()
    
  }
}, 3000); // 3000 milliseconds = 3 seconds



  return (
    <div>
    
      {/* <h1>Book Summary App</h1> */}
      <form onSubmit={handleSubmit} className='my-5'>
        <input style={{width:'70%'}}
          type="text"
          value={bookName}
          onChange={(event) => setBookName(event.target.value)}
          placeholder="Enter book name"

        />
        <button type="submit">send</button>
      </form>
      {bookInfo && (
        
        <div className='container'>
            {/* <script src="https://cdn.jsdelivr.net/npm/marked@4.0.1/dist/marked.min.js"></script> */}
            {/* <script>{setBookInfo(marked.parse(bookInfo.data))}</script> */}
          <h2>{bookName}</h2>

            {/* <img src=	"https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1470282721i/29502358.jpg" style={{maxWidth:'200px',maxHeight:'500px'}} alt="" /> */}
            
            <img src={bookUrl} style={{maxWidth:'200px',maxHeight:'500px',border:'2px solid black'}} alt="" />


        <div className='my-5' dangerouslySetInnerHTML={{__html:bookInfo}} style={{border:'5px solid black', textAlign:'left', marginRight:'50px', padding:'5px',fontFamily:'serif'}}/>
{/* 
        <div className='my-5'  style={{border:'5px solid black', textAlign:'left', marginRight:'50px', padding:'5px',fontFamily:'serif'}}/>
        {{bookInfo}} */}
         <TypeAnimation
        splitter={(str) => str.split(/(?= )/)} // 'Lorem ipsum dolor' -> ['Lorem', ' ipsum', ' dolor']
        sequence={[
          'Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, no sea takimata sanctus est Lorem ipsum dolor sit amet. Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua. At vero eos et accusam et justo duo dolores et ea rebum. Stet clita kasd gubergren, no sea takimata sanctus est Lorem ipsum dolor sit amet.',
          3000,
          '',
        ]}
        speed={{ type: 'keyStrokeDelayInMs', value: 30 }}
        omitDeletionAnimation={true}
        style={{ fontSize: '1em', display: 'block', minHeight: '200px',textAlign:'left' }}
        repeat={Infinity}
      />
        </div>
      )}
      {/* <div className='class="img-fluid mr-3"'> */}
     
      <div id="typewriter"></div>

      {/* </div> */}
    </div>
    
  );
}

export default Book;
