import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { marked } from 'marked';
import Container from 'react-bootstrap/esm/Container';
import { TypeAnimation } from 'react-type-animation';

function Book() {
  const [bookName, setBookName] = useState('');
  const [bookInfo, setBookInfo] = useState('');
  const [bookUrl, setBookUrl] = useState('');
  const [typingText, setTypingText] = useState('');

  useEffect(() => {
    if (bookInfo) {
      setTypingText(bookInfo);
    }
  }, [bookInfo]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://127.0.0.1:5000/info/${bookName}`);
      setBookInfo(response.data.data);
      setBookUrl(response.data.cover_img);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className='my-5'>
        <input
          type="text"
          value={bookName}
          onChange={(event) => setBookName(event.target.value)}
          placeholder="Enter book name"
        />
        <button type="submit">send</button>
      </form>
      {bookInfo && (
        <div className='container'>
          <img src={bookUrl} style={{ maxWidth: '200px', maxHeight: '400px', border: '2px solid black' }} alt="" />
          <div id='bookdata' style={{ border: '5px solid black', textAlign: 'left', marginRight: '50px', padding: '5px', fontFamily: 'serif' }}>
            <TypeAnimation
              sequence={[typingText, 3000]}
              speed={{ type: 'keyStrokeDelayInMs', value: 10 }}
              omitDeletionAnimation={true}
              style={{ whiteSpace: 'pre-line', fontSize: '2em', display: 'block', minHeight: '200px' }}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Book;
