import React, { useState } from 'react';
import axios from 'axios';
import { marked } from 'marked';
// import Markdown from 'marked-react';
// import marked from 'marked';

function Book() {
  const [bookName, setBookName] = useState('');
  const [bookInfo, setBookInfo] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://127.0.0.1:5000/info/${bookName}`);
      setBookInfo(marked(response.data.data));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {/* <h1>Book Summary App</h1> */}
      <form onSubmit={handleSubmit} className='my-5'>
        <input style={{width:'70%'}}
          type="text"
          value={bookName}
          onChange={(event) => setBookName(event.target.value)}
          placeholder="Enter book name"

        />
        <button type="submit">send</button>
      </form>
      {bookInfo && (
        <div>
            {/* <script src="https://cdn.jsdelivr.net/npm/marked@4.0.1/dist/marked.min.js"></script> */}
            {/* <script>{setBookInfo(marked.parse(bookInfo.data))}</script> */}

          <h2>{bookName}</h2>
          {/* <p dangerouslySetInnerHTML={{ __html: bookInfo }} /> */}
          {/* <pre style={{textAlign:'left', marginRight:'50px', padding:'5px',fontFamily:'serif'}}>{bookInfo}</pre> */}
        {/* {bookInfo} */}
        <div dangerouslySetInnerHTML={{__html:bookInfo}} style={{textAlign:'left', marginRight:'50px', padding:'5px',fontFamily:'serif'}}/>
        </div>
      )}
      {/* <div className='class="img-fluid mr-3"'> */}
      <img className='class="img-fluid mr-3"' src=	"https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1470282721i/29502358.jpg" style={{maxWidth:'200px',maxHeight:'500px'}} alt="" />

      {/* </div> */}
    </div>
    
  );
}

export default Book;
